jQuery(document).ready(function($){$('img.lazy,img.avatar').lazyload();if(bowser.mobile){$('.mobile-menu-button').on('touchstart',function(event){$('body').hasClass('mobile-menu-open')?$('body').removeClass('mobile-menu-open'):$('body').addClass('mobile-menu-open');});}else{var hstatus=false,htimer;$('.dropdown').mouseenter(function(event){var $this=$(this);if(htimer){clearTimeout(htimer);htimer=null;}
if(!$this.hasClass('selected')){$this.addClass('selected');hstatus=true;}});$('.dropdown').mouseleave(function(event){var $this=$(this);if($this.hasClass('selected')&&hstatus){htimer=setTimeout(function(){$this.removeClass('selected');hstatus=false;clearTimeout(htimer);htimer=null;},100);}});}});
